public class ConversationSettingRequest {

    @SerializedName("participant")
    public Participant mParticipant = null;

    @SerializedName("notify_enabled")
    public Boolean mNotifyEnable = null;

    @SerializedName("weixin_sync_enabled")
    public Boolean mWeChatSyncEnable = null;


    public static class Participant {

        @SerializedName("domain_id")
        public String mDomainId;

        @SerializedName("client_id")
        public String mClientId;

        @SerializedName("type")
        public String mType;
    }
}
