import android.content.Context;
import android.os.Handler;

import com.ies.link.IESException;

import java.io.IOException;
import java.io.InputStream;
import java.io.OutputStream;
import java.net.InetSocketAddress;
import java.security.KeyManagementException;
import java.security.KeyStoreException;
import java.security.NoSuchAlgorithmException;
import java.security.UnrecoverableKeyException;

/**
 * Created by reyzhang22 on 16/1/12.
 */
public interface SocketStrategy {

    void initSocket(Context context, Handler handler, InetSocketAddress socketAddress, int timeout, boolean sslVerify) throws IOException, IESException, KeyManagementException, NoSuchAlgorithmException, UnrecoverableKeyException, KeyStoreException;

    OutputStream getOutputStream() throws IOException;

    InputStream getInputStream() throws IOException;

    boolean isConnect();

    boolean isClose();

    void closeSocket() throws IOException;
}
