/**
 * 应用类型（同一个应用可以分为多个类型）
 * Access  0  (访问应用)
 * Admin  1 (管理应用)
 * Created by reyzhang22 on 16/4/14.
 */
public enum AppType {
    Access {
        @Override
        public int intValue() {
            return 0;
        }
    },
    Admin {
        @Override
        public int intValue() {
            return 1;
        }
    };

    public abstract int intValue();

    public static AppType toAppType(int type) {
        if (Access.intValue() == type) {
            return Access;
        }
        return Admin;
    }
}
