/**
 * 是否强制安装枚举
 * Created by reyzhang22 on 16/4/14.
 */
public enum DistributeMode {
    /**
     * 强制安装
     */
    Enforce {
        @Override
        public int intValue() {
            return 1;
        }
    },
    /**
     * 非强制安装
     */
    NoEnforce {
        @Override
        public int intValue() {
            return 0;
        }
    };

    public abstract int intValue();

    public static DistributeMode getDisplayMode(int mode) {
        if (mode == 1) {
            return Enforce;
        }
        return NoEnforce;
    }
}
