/**
 * 推荐应用枚举
 * Created by reyzhang22 on 16/4/14.
 */
public enum RecommendMode {

    /**
     * 推荐模式
     */
    Recommend {
        @Override
        public int intValue() {
            return 1;
        }
    },
    /**
     * 非推荐模式
     */
    NoRecommend {
        @Override
        public int intValue() {
            return 0;
        }
    };

    public abstract int intValue();

    public static RecommendMode toRecommendMode(int mode) {
        if (mode == 0) {
            return NoRecommend;
        }
        return Recommend;
    }
}
