import android.os.Parcelable;

/**
 * 分享抽象类，最基本的分享数据
 * Created by reyzhang22 on 16/6/27.
 */
public abstract class ShareModel implements Parcelable {

    /**
     * 分享链接
     */
    public String mUrl;

    /**
     * 分享标题
     */
    public String mTitle;

    /**
     * 分享简介
     */
    public String mSummary;

    /**
     * 分享标题图片
     */
    public String mTitleImage;


}
