/**
 * Created by reyzhang22 on 16/4/21.
 */
public enum Operation {
    /**
     * app不在范围, 应该做删除处理
     */
    APP_NOT_FOUND,

    CREATE,

    /**
     * 未知类型
     * */
    UNKNOWN;


    public static Operation fromStringValue(String value) {

        if ("APP_NOT_FOUND".equalsIgnoreCase(value)) {
            return APP_NOT_FOUND;
        }

        if ("CREATE".equalsIgnoreCase(value)) {
            return CREATE;
        }

        return UNKNOWN;
    }
}
