import androidx.annotation.NonNull;

import com.foreveross.atwork.infrastructure.newmessage.post.chat.TextChatMessage;
import com.google.gson.Gson;
import com.google.gson.GsonBuilder;

/**
 * Created by reyzhang22 on 16/4/21.
 */
public class GsonHelper {

    @NonNull
    public static Gson getMessageMapGson() {
        GsonBuilder gsonBuilder = new GsonBuilder().excludeFieldsWithoutExposeAnnotation();
        gsonBuilder.registerTypeAdapter(byte[].class, new ByteArrayToBase64TypeAdapter())
                .registerTypeAdapter(TextChatMessage.AtUser.class, new AtUserTypeAdapter());

        return gsonBuilder.create();
    }

}
